(43, 'document', 'text/html', 'Спасибо за регистрацию!', '', '', 'register-thankyou', '', 1, 0, 0, 39, 0, '', '<p>\nВам отправлено письмо для активации аккаунта.\n</p>', 1, 6, 2, 1, 1, 1, 1487524536, 1, 1487524557, 0, 0, 0, 1487524500, 1, '', 0, 0, 0, 0, 1, 'modDocument', 'web', 1, 'auth/register-thankyou.html', 0, 0, 1, NULL),
