(1, 1, 0, 'header', '', 0, 0, 0, '    <header>\n        <nav class="navbar navbar-default" role="navigation">\n            <div class="">\n                <div class="navbar-header header-image">\n                    <button type="button" class="navbar-toggle" data-toggle="collapse" data-target="#bs-example-navbar-collapse-1">\n                        <span class="sr-only">Меню</span>\n                        <span class="icon-bar"></span>\n                        <span class="icon-bar"></span>\n                        <span class="icon-bar"></span>\n                    </button>\n                    <div class="header-right">\n\n                        <div class="pull-right">\n                            [[!+modx.user.id:gt=`0`:then=`\n                            <a href="[[~39]]">\n                                <span data-toggle="tooltip" title="Профиль">\n                                    <i class="icon" data-icon="&#xe0b4"></i>\n                                    [[!+modx.user.id:userinfo=`email`]]\n                                </span>\n                            </a>\n                            `:else=`\n                            <a href="[[~39]]">\n                                <i class="icon" data-icon="&#xe098" data-toggle="tooltip" title="Войти"></i>\n                            </a>\n                            `]]\n                        </div>\n\n                        [[!compare@compare_options]]\n                        \n                        [[!Shopkeeper3@cart_catalog]]\n                        \n                    </div>\n                    <a class="navbar-brand" href="[[++base_url]]">\n                        [[++site_name]]\n                    </a>\n                </div>\n                <div class="collapse navbar-collapse" id="bs-example-navbar-collapse-1">\n                    \n                    [[menuCaching@top_menu]]\n\n                    <div class="navbar-form-right navbar-right">\n                        <div class="row">\n                            <div class="col-md-8 col-xs-8">\n                                <form action="[[~11]]" method="get"role="search">\n                                    <div class="form-search search-only">\n                                        <i class="search-icon glyphicon glyphicon-search"></i>\n                                        <input type="search" name="f_pagetitle[like]" class="form-control search-query" value="[[!getRequest?paramName=`f_pagetitle.like`]]">\n                                    </div>\n                                </form>\n                            </div>\n                            <div class="col-md-4 col-xs-4">\n                                <select class="form-control" id="site_currency" name="curency">\n                                    <option value="1">руб.</option>\n                                    <option value="2">грн.</option>\n                                    <option value="3">USD</option>\n                                    <option value="4">euro</option>\n                                </select>\n                            </div>\n                        </div>\n                    </div>\n\n                </div>\n            </div>\n        </nav>\n    </header>\n', 0, 'a:0:{}', 1, 'assets/template/header.html'),
(51, 1, 0, 'head', '', 0, 0, 0, '<!doctype html>\n<html>\n<head>\n    <meta http-equiv="X-UA-Compatible" content="IE=edge" />\n    <meta charset="utf-8">\n    <meta name="viewport" content="width=device-width, initial-scale=1">\n    <title>[[++site_name]] / [[*pagetitle]]</title>\n    <meta name="description" content="[[*introtext:htmlent]]">\n\n    <link rel="stylesheet" href="[[++base_url]]assets/template/dist/css/styles_all.min.css">\n    <script type="text/javascript" src="[[++base_url]]assets/template/dist/js/scripts_all.min.js"></script>\n</head>\n<body>\n\n<div class="container">', 0, 'a:0:{}', 1, 'assets/template/head.html'),
(52, 1, 0, 'header2', '', 0, 0, 0, '    <header>\n        <nav class="navbar navbar-default" role="navigation">\n            <div class="">\n                <div class="navbar-header header-image">\n                    <button type="button" class="navbar-toggle" data-toggle="collapse" data-target="#bs-example-navbar-collapse-1">\n                        <span class="sr-only">Меню</span>\n                        <span class="icon-bar"></span>\n                        <span class="icon-bar"></span>\n                        <span class="icon-bar"></span>\n                    </button>\n                    <div class="header-right">\n\n                        <div class="pull-right">\n                            [[!+modx.user.id:gt=`0`:then=`\n                            <a href="[[~39]]">\n                                <span data-toggle="tooltip" title="Профиль">\n                                    <i class="icon" data-icon="&#xe0b4"></i>\n                                    [[!+modx.user.id:userinfo=`email`]]\n                                </span>\n                            </a>\n                            `:else=`\n                            <a href="[[~39]]">\n                                <i class="icon" data-icon="&#xe098" data-toggle="tooltip" title="Войти"></i>\n                            </a>\n                            `]]\n                        </div>\n\n                        [[!compare@compare_options]]\n                        \n                        [[!Shopkeeper3@cart_catalog_modx_resources]]\n                        \n                    </div>\n                    <a class="navbar-brand" href="[[++base_url]]">\n                        [[++site_name]]\n                    </a>\n                </div>\n                <div class="collapse navbar-collapse" id="bs-example-navbar-collapse-1">\n                    \n                    [[menuCaching@top_menu]]\n\n                    <div class="navbar-form-right navbar-right">\n                        <div class="row">\n                            <div class="col-md-8 col-xs-8">\n                                <form action="[[~11]]" method="get"role="search">\n                                    <div class="form-search search-only">\n                                        <i class="search-icon glyphicon glyphicon-search"></i>\n                                        <input type="search" name="f_pagetitle[like]" class="form-control search-query" value="[[!getRequest?paramName=`f_pagetitle.like`]]">\n                                    </div>\n                                </form>\n                            </div>\n                            <div class="col-md-4 col-xs-4">\n                                <select class="form-control" id="site_currency" name="curency">\n                                    <option value="1">руб.</option>\n                                    <option value="2">грн.</option>\n                                    <option value="3">USD</option>\n                                    <option value="4">euro</option>\n                                </select>\n                            </div>\n                        </div>\n                    </div>\n                </div>\n            </div>\n        </nav>\n    </header>\n', 0, 'a:0:{}', 1, 'assets/template/header2.html'),
(45, 1, 0, 'page_feedback_form', '', 0, 13, 0, '\n<p>Будем рады Вашим вопросам, замечаниям и предложениям. Все поля обязательны для заполнения.</p>\n\n<hr>\n\n[[!FormIt?\n&preHooks=`coolcaptcha_fihook`\n&hooks=`coolcaptcha_fihook,spam,email,FormItSaveForm`\n&submitVar=`action`\n&validate=`fullname:required,email:email:required,text:required:stripTags`\n&emailTpl=`feedbackEmailChunk`\n&emailSubject=`Письмо с сайта`\n&emailReplyTo=`[[+email]]`\n&emailFrom=`[[++emailsender]]`\n&emailTo=`[[++emailsender]]`\n&invalidCaptchaMessage=`<div class="alert alert-danger">Не верный код подтверждения.</div>`\n&validationErrorMessage=`<div class="alert alert-danger">Ошибка заполнения формы. Пожалуйста, проверьте значения, введенные вами.</div>`\n&errTpl=`<p>[[+error]]</p>`\n&successMessage=`<div class="alert alert-success">Спасибо! Ваше письмо отправлено.</div>`\n&formFields=`fullname,email,text`\n]]\n\n<a name="form"></a>\n[[!+fi.validation_error_message]]\n[[!+fi.successMessage]]\n[[!+fi.error.captcha]]\n\n<div class="row">\n    <div class="col-md-6 col-md-offset-3">\n\n        <form class="form-horizontal" id="feedbackForm" action="[[~[[*id]]]]#form" method="post">\n            <input type="hidden" name="action" value="feedback">\n            <div class="form-group">\n                <label for="feedbackFormFieldName" class="col-sm-4 control-label">Ваше имя:</label>\n                <div class="col-sm-8">\n                    <input type="text" class="form-control" id="feedbackFormFieldName" name="fullname" placeholder="Введите Ваше имя" value="[[!+fi.fullname]]" required>\n                </div>\n            </div>\n            <div class="form-group">\n                <label for="feedbackFormFieldEmail" class="col-sm-4 control-label">Ваш адрес эл. почты:</label>\n                <div class="col-sm-8">\n                    <input type="email" class="form-control" id="feedbackFormFieldEmail" name="email" placeholder="Введите Ваш Email" value="[[!+fi.email]]" required>\n                </div>\n            </div>\n            <div class="form-group">\n                <label for="feedbackFormFieldText" class="col-sm-4 control-label">Сообщение:</label>\n                <div class="col-sm-8">\n                    <textarea id="feedbackFormFieldText" class="form-control" cols="40" rows="5" name="text" placeholder="Введите текст письма" required>[[!+fi.text]]</textarea>\n                </div>\n            </div>\n            <div class="form-group">\n                <div class="col-sm-8 col-sm-offset-4">\n                    [[!+fi.coolcaptcha]]\n                    <input type="text" class="form-control" name="captcha" id="feedbackFormFieldCaptcha" placeholder="Код подтверждения" autocomplete="on" required>\n                </div>\n            </div>\n            <div class="form-group">\n                <div class="col-sm-offset-4 col-sm-10">\n                    <button type="submit" class="btn btn-primary btn-lg">\n                        Отправить\n                    </button>\n                </div>\n            </div>\n        </form>\n\n    </div>\n</div>', 0, 'a:0:{}', 1, 'assets/template/page_feedback_form.html'),
