(39, 0, 0, 'page_order_cart', '', 0, 13, 0, '\n[[!Shopkeeper3@cart_order_page]]\n\n[[!FormIt?\n&hooks=`spam,shk_fihook,email,FormItAutoResponder,redirect`\n&submitVar=`order`\n&emailTpl=`shopOrderReport`\n&fiarTpl=`shopOrderReport`\n&emailSubject=`В интернет-магазине "[[++site_name]]" сделан новый заказ`\n&fiarSubject=`Вы сделали заказ в интернет-магазине "[[++site_name]]"`\n&emailTo=`[[++emailsender]]`\n&fiarReplyTo=`[[++emailsender]]`\n&fiarToField=`email`\n&redirectTo=`12`\n&validate=`address:required,fullname:required,email:email:required,phone:required`\n&errTpl=`<span class="error">[[+error]]</span>`\n]]\n\n[[!+shk.items_total:gt=`0`:then=`\n[[$shopOrderForm_modified]]\n`:else=``]]\n\n<script type="text/javascript">\n    function SHKloadCartCallback() {\n        if( $(\'#shopOrderForm\').length > 0 && SHK.data.items_total == 0 ){\n            window.location.reload();\n        }\n    }\n</script>', 0, 'a:0:{}', 0, ''),
(40, 0, 0, 'shopOrderForm_modified', '', 0, 12, 0, '\n[[!shkOptions?\n&get=`delivery,payments`\n&post_name=`shk_delivery,payment`\n&toPlaceholders=`1`\n&pl_prefix=`shkopt_`\n&tpl=`select_option`\n]]\n\n<p class="error">[[!+fi.error.error_message]]</p>\n<br />\n\n<div class="row">\n    <div class="col-md-6">\n        \n        [[!giftCards?\n        &tpl=`giftCards_form`\n        &max_attempts=`10`\n        ]]\n        \n    </div>\n    <div class="col-md-6">\n\n        <form method="post" action="[[~[[*id]]]]" id="shopOrderForm">\n\n        <fieldset>\n\n        <input type="text" name="nospam:blank" value="" style="display:none;" />\n        <input type="hidden" name="order" value="1" />\n\n        <div class="form-group">\n            <label for="orderFormName">\n                Ф.И.О.*:\n            </label>\n            <input name="fullname" size="30" id="orderFormName" class="form-control" type="text" value="[[!+fi.fullname:default=`[[+modx.user.id:userinfo=`fullname`]]`:ne=`0`:show]]" />\n            <p class="help-block">\n                [[!+fi.error.fullname]]\n            </p>\n        </div>\n\n        <div class="form-group">\n            <label for="orderFormName">\n                Адрес*:\n            </label>\n            <input name="address" size="30" class="form-control" type="text" value="[[!+fi.address:default=`[[+modx.user.id:userinfo=`address`]]`:ne=`0`:show]]" />\n            <p class="help-block">\n                [[!+fi.error.address]]\n            </p>\n        </div>\n\n        <div class="row form-group">\n            <div class="col-md-6">\n            \n                <label for="orderFormName">\n                    E-mail*:\n                </label>\n                <input name="email" size="30" class="form-control" type="text" value="[[!+fi.email:default=`[[+modx.user.id:userinfo=`email`]]`:ne=`0`:show]]" />\n                <p class="help-block">\n                    [[!+fi.error.email]]\n                </p>\n                \n            </div>\n            <div class="col-md-6">\n            \n                <label for="orderFormName">\n                    Телефон*:\n                </label>\n                <input name="phone" size="30" class="form-control" type="text" value="[[!+fi.phone:default=`[[+modx.user.id:userinfo=`phone`]]`:ne=`0`:show]]" />\n                <p class="help-block">\n                    [[!+fi.error.phone]]\n                </p>\n            \n            </div>\n        </div>\n\n        <div class="row form-group">\n            <div class="col-md-6">\n                \n                <label for="orderFormName">\n                    Способ доставки*:\n                </label>\n                <select name="shk_delivery" class="form-control">\n                    <option value=""></option>\n                    [[!+shkopt_delivery]]\n                </select>\n                <p class="help-block">\n                    [[!+fi.error.shk_delivery]]\n                </p>\n                \n            </div>\n            <div class="col-md-6">\n                \n                <label for="orderFormName">\n                    Способ оплаты*:\n                </label>\n                <select name="payment" class="form-control">\n                    <option value=""></option>\n                    [[!+shkopt_payments]]\n                </select>\n                <p class="help-block">\n                    [[!+fi.error.payment]]\n                </p>\n                \n            </div>\n        </div>\n\n        <div class="form-group">\n            <label for="orderFormName">\n                Комментарий:\n            </label>\n            <textarea name="message" class="form-control" rows="4" cols="30">[[!+fi.message]]</textarea>\n        </div>\n\n        <div class="form-group">\n            <input type="submit" name="submit_button" class="button btn btn-primary btn-lg" value="Отправить" />\n        </div>\n\n        </fieldset>\n\n        </form>\n\n    </div>\n</div>\n\n<script type="text/javascript">\n$(document).bind(\'ready\',function(){\n    if ( SHK.data.delivery_name ) {\n        $(\'select[name="shk_delivery"]\',\'#shopOrderForm\').val( SHK.data.delivery_name );\n    }\n    $(\'select[name="shk_delivery"]\',\'#shopOrderForm\').bind(\'change\',function(){\n        SHK.selectDelivery( $(this).val() );\n    });\n});\n</script>\n', 0, 'a:0:{}', 0, ''),
(41, 0, 0, 'shopCart_modified', '', 0, 12, 0, '<div data-shopcart="2">\n    <div class="col-sm-12 text-center">\n        <br><br><br>\n        <div style="font-size:60px;" class="icon icon-shopping-cart"></div>\n        <br><br>\n        Ваша корзина пуста.\n    </div>\n</div>\n<!--tpl_separator-->\n<div id="shopCart" class="panel panel-default" data-shopcart="2">\n    <div class="panel-heading">\n        <a name="shopCart"></a>\n        <div class="pull-right">\n            <a id="shk_butEmptyCart" href="[[+empty_url]]" class="badge">Очистить</a>\n        </div>\n        Корзина\n    </div>\n    <div class="panel-body">\n        <div class="table-responsive">\n        <form action="[[+this_page_url]]#shopCart" method="post">\n            <table class="table no-margin">\n                <colgroup>\n                    <col width="10%">\n                    <col width="20%">\n                    <col width="30%">\n                    <col width="15%">\n                    <col width="20%">\n                    <col width="5%">\n                </colgroup>\n                <tbody>\n                    [[+inner]]\n                </tbody>\n            </table>\n            </div>\n            <hr>\n            <div class="pull-right">\n                <table width="400">\n                    <tr>\n                        <td class="text-right">\n                            [[+delivery_name]]:\n                        </td>\n                        <td class="text-right">\n                            [[+delivery_price]] руб.\n                        </td>\n                    </tr>\n                    <tr>\n                        <td class="text-right">\n                            Общая сумма:\n                        </td>\n                        <td class="text-right">\n                            <b>[[+price_total]]</b> руб.\n                        </td>\n                    </tr>\n                </table>\n            </div>\n            <div class="clearfix"><