(50, 1, 0, 'product_list2', '', 0, 8, 0, '<div class="col-sm-12">\n    <div class="thumbnail shk-item">\n        <div class="caption">\n            <form action="[[~[[*id]]? &scheme=`abs`]]" method="post">\n                <div class="pull-right">\n                    <div class="btn-group" role="group">\n                        <a href="[[~[[+resource_id]]?&scheme=`abs`]][[+alias]].html" class="btn btn-success" role="button">Подробнее</a>\n                        <button type="submit" class="btn btn-default" data-toggle="tooltip" data-placement="top" title="В корзину">\n                            <i class="icon" data-icon="&#xe052"></i>\n                        </button>\n                    </div>\n                </div>\n                <input type="hidden" name="shk-id" value="[[+id]]" />\n                <input type="hidden" name="shk-name" value="[[+pagetitle]]" />\n                <input type="hidden" name="shk-count" value="1" />\n                <h3>\n                    <a href="[[~[[+id]]?&scheme=`abs`]]">[[+pagetitle]]</a>\n                    &nbsp;\n                    <span class="buttons-hover-opacity">\n                        <button type="button" class="btn btn-sm btn-default[[+id:in_compare=` active`]]" data-toggle="tooltip" data-placement="bottom" title="В избранное" onclick="return shkCompare.toCompare([[+id]],[[+resource_id]],this)">\n                            <i class="icon" data-icon="&#xe089"></i>\n                        </button>\n                    </span>\n                </h3>\n                <p>[[+introtext]]</p>\n                <div class="clearfix"></div>\n            </form>\n        </div>\n    </div>\n</div>', 0, 'a:0:{}', 1, 'assets/template/chunk.product_list2.html'),
