(62, 1, 0, 'lgnRegisterFormTpl_modified', '', 0, 16, 0, '<div class="row">\n    <div class="col-md-6 col-md-offset-3">\n        [[+error.message]]\n        <form class="form" action="[[~[[*id]]]]" method="post" class="form-horizontal">\n            <input type="hidden" name="nospam:blank" value="" />\n\n            <div class="row form-group">\n                <label for="formFieldFullname" class="col-sm-5 control-label">\n                    [[%register.fullname]]\n                </label>\n                <div class="col-sm-7">\n                    <input id="formFieldFullname" class="form-control" type="text" name="fullname:required" value="[[+fullname]]">\n                </div>\n                <div class="col-xs-12">\n                    [[+error.fullname]]\n                </div>\n            </div>\n\n            <div class="row form-group">\n                <label for="formFieldEmail" class="col-sm-5 control-label">\n                    [[%register.email]]\n                </label>\n                <div class="col-sm-7">\n                    <input id="formFieldEmail" class="form-control" type="text" name="email:email" value="[[+email]]">\n                </div>\n                <div class="col-xs-12">\n                    [[+error.email]]\n                </div>\n            </div>\n\n            <div class="row form-group">\n                <label for="formFieldPassword" class="col-sm-5 control-label">\n                    [[%register.password]]\n                </label>\n                <div class="col-sm-7">\n                    <input id="formFieldPassword" class="form-control" type="password" name="password:required:minLength=6" value="[[+password]]">\n                </div>\n                <div class="col-xs-12">\n                    [[+error.password]]\n                </div>\n            </div>\n\n            <div class="row form-group">\n                <label for="formFieldPasswordRe" class="col-sm-5 control-label">\n                    [[%regis