(67, 1, 0, 'lgnLoginTpl_modified', '', 0, 16, 0, '<div class="row">\n    <div class="col-md-4 col-md-offset-4">\n        [[+errors]]\n        <div class="loginLogin">\n            <form class="loginLoginForm" action="[[~[[*id]]]]" method="post">\n\n                <input class="returnUrl" type="hidden" name="returnUrl" value="[[+request_uri]]">\n                <input class="loginLoginValue" type="hidden" name="service" value="login">\n\n                <div class="form-group">\n                    <label class="form-label" for="loginFieldUsername">[[%login.username]]</label>\n                    <input id="loginFieldUsername" class="form-control" type="text" name="username" />\n                </div>\n\n                <div class="form-group">\n                    <label class="form-label" for="loginFieldPassword">[[%login.password]]</label>\n                    <input id="loginFieldPassword" class="form-control" type="password" name="password">\n                </div>\n\n                <div class="form-group">\n                    <button class="btn btn-primary" type="submit" name="Login" value="[[+actionMsg]]">\n                        [[+actionMsg]]\n                    </button>\n                </div>\n\n                <hr>\n\n                <a href="[[~42]]">\n                    Забыли пароль?\n                </a>\n                &nbsp;&bull;&nbsp;\n                <a href="[[~40]]">\n                    Регистрация\n                </a>\n\n            </form>\n        </div>\n    </div>\n</div>', 0, 'a:0:{}', 1, 'assets/template/chunk_lgnLoginTpl_modified.html'),
