(71, 1, 0, 'lgnUpdateProfile_modified', '', 0, 16, 0, '<div style="float: right; position: relative; top: -45px; right: 0;">\n    <a class="btn btn-info" href="[[~39]]?service=logout">\n        Выйти\n    </a>\n</div>\n\n<div class="row">\n    <div class="col-md-6 col-md-offset-3">\n\n        [[+login.update_success:is=`1`:then=`\n        <div class="alert alert-success">\n            [[%login.profile_updated? &namespace=`login` &topic=`updateprofile`]]\n        </div>\n        `]]\n\n        <form class="form" action="[[~[[*id]]]]" method="post">\n            <input type="hidden" name="nospam" value="" />\n\n            <div class="row form-group">\n                <label for="formFieldFullName" class="col-sm-5 control-label">\n                    [[!%login.fullname? &namespace=`login` &topic=`updateprofile`]]\n                </label>\n                <div class="col-sm-7">\n                    <input id="formFieldFullName" class="form-control" type="text" name="fullname" value="[[+fullname]]">\n                </div>\n                <div class="col-xs-12">\n                    [[+error.fullname]]\n                </div>\n            </div>\n\n            <div class="row form-group">\n                <label for="formFieldEmail" class="col-sm-5 control-label">\n                    [[!%login.email]]\n                </label>\n                <div class="col-sm-7">\n                    <input id="formFieldEmail" class="form-control" type="text" name="email" value="[[+email]]">\n                </div>\n                <div class="col-xs-12">\n                    [[+error.email]]\n                </div>\n            </div>\n\n            <div class="row form-group">\n                <label for="formFieldPhone" class="col-sm-5 control-label">\n                    [[!%login.mobilephone]]\n                </label>\n                <div class="col-sm-7">\n                    <input id="formFieldPhone" class="form-control" type="text" name="mobilephone" value="[[+mobilepho