(5, 1, 0, 'shk_updateInventory', 'Update inventory data.', 0, 5, 0, '/*\n * shk_updateInventory\n * \n * Плагин обновляет количество товара на складе при переводе статуса заказа в "Отправлен" (или др.)\n * \n * OnSHKChangeStatus\n */\n\n$eventName = $modx->event->name;\n\n$inventory_fieldname = $modx->getOption( \'inventory_fieldname\', $scriptProperties, \'inventory\' );\n$plugin_status = $modx->getOption( \'plugin_status\', $scriptProperties, \'2\' );\n$context = $modx->getOption( \'context\', $scriptProperties, \'\' );\n$order_ids = $modx->getOption( \'order_ids\', $scriptProperties, array() );\nif( !is_array( $order_ids ) ){\n    $order_ids = $order_ids && is_numeric( $order_ids ) ? array( $order_ids ) : array();\n}\n$status = $modx->getOption( \'status\', $scriptProperties, \'\' );\n\nif( empty( $order_ids ) || empty( $status ) || $status != $plugin_status ) return \'\';\n\n$modelpath = $modx->getOption(\'core_path\') . \'components/shopkeeper3/model/\';\n$modx->addPackage( \'shopkeeper3\', $modelpath );\n\nforeach( $order_ids as $order_id ){\n    \n    $query = $modx->newQuery( \'shk_purchases\' );\n    $query->where( array( \'order_id\' => $order_id ) );\n    $purchases = $modx->getIterator( \'shk_purchases\', $query );\n    \n    if( $purchases ){\n        \n        foreach( $purchases as $purchase ){\n            \n            if( $purchase->package_name && $purchase->package_name != \'modResource\' && !in_array( $purchase->package_name, array_keys($modx->packages) ) ){\n                $modelpath = $modx->getOption(\'core_path\') . \'components/\' . $purchase->package_name . \'/model/\';\n                $modx->addPackage( $purchase->package_name, $modelpath );\n            }\n            \n            $product = $modx->getObject( $purchase->class_name, $purchase->p_id );\n            if( $product ){\n                \n                $p_data = $product->toArray();\n                \n                //Если это поле основной таблицы\n                if( isset( $p_data[ $inventory_fieldname ] ) ){\n                    \n                    $current_inventory = $p_data[ $inventory_fieldname ];\n                    $current_inventory = $current_inventory ? floatval( $current_inventory ) : 0;\n                    if( !$current_inventory ) continue;\n                    \n                    $new_inventory = $current_inventory - $purchase->count;\n                    if( $new_inventory < 0 ) $new_inventory = 0;\n                    \n                    $product->set( $inventory_fieldname, $new_inventory );\n                    $product->save();\n                    \n                }\n                //Если значение хранится в TV параметре\n                else{\n                    \n                    $table_name = \'\';\n                    if ( $className = $modx->loadClass( $purchase->class_name ) ) {\n                        $table_name = isset( $modx->map[$className][\'table\'] ) ? $modx->map[$className][\'table\'] : \'\';\n                    }\n                    if( $table_name != \'site_content\' ) continue;\n                    \n                    $context = $p_data[\'context_key\'];\n                    \n                    $tv = $modx->getObject( \'modTemplateVar\', array( \'name\' => $inventory_fieldname ));\n                    if( !$tv ) continue;\n                    \n                    $current_inventory = $tv->getValue( $p_data[\'id\'] );\n                    $current_inventory = $current_inventory ? floatval( $current_inventory ) : 0;\n                    if( !$current_inventory ) continue;\n                    \n                    $new_inventory = $current_inventory - $purchase->count;\n                    if( $new_inventory < 0 ) $new_inventory = 0;\n                    \n                    //$tv->setValue( $p_data[\'id\'], $new_inventory );\n                    \n                    $templateVarResource = $modx->getObject(\'modTemplateVarResource\',array(\n                        \'tmplvarid\' => $tv->get(\'id\'),\n                        \'contentid\' => $p_data[\'id\'],\n                    ),true);\n                    \n                    if( $templateVarResource ){\n                        $templateVarResource->set( \'value\', $new_inventory );\n                        $templateVarResource->save();\n                    }\n                    \n                }\n                \n            }\n            \n        }\n        \n    }\n    \n}\n\n\n//Очистка кэша сайта\nif( $context ){\n    $modx->cacheManager->refresh(array(\n        \'resource\' => array( \'contexts\' => array( $context ) ),\n    ));\n}\n\nreturn \'\';', 0, 'a:3:{s:13:"plugin_status";a:7:{s:4:"name";s:13:"plugin_status";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:1:"2";s:7:"lexicon";N;s:4:"area";s:0:"";}s:19:"inventory_fieldname";a:7:{s:4:"name";s:19:"inventory_fieldname";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:9:"inventory";s:7:"lexicon";N;s:4:"area";s:0:"";}s:7:"context";a:7:{s:4:"name";s:7:"context";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:3:"web";s:7:"lexicon";N;s:4:"area";s:0:"";}}', 1, '', 0, ''),
(6, 1, 0, 'shk_tv_input_output', 'Print Shopkeeper`s input and output types for TV.', 0, 5, 0, '$corePath = MODX_CORE_PATH.\'components/shopkeeper3/\';\n\nswitch ($modx->event->name) {\n    //tv input\n    case \'OnTVInputRenderList\':\n        if($modx->context->get(\'key\') != \'mgr\') return;\n        $modx->regClientStartupScript($modx->getOption(\'assets_url\').\'components/shopkeeper3/mgr/js/shk_mgr.js\');\n        $modx->regClientStartupScript($modx->getOption(\'assets_url\').\'components/shopkeeper3/mgr/js/widgets/shk.grid.js\');\n        $modx->regClientCSS($modx->getOption(\'assets_url\').\'components/shopkeeper3/mgr/css/mgr.css\');\n        $modx->event->output($corePath.\'elements/tv/input/\');\n    break;\n    case \'OnTVInputPropertiesList\':\n        if($modx->context->get(\'key\') != \'mgr\') return;\n        $modx->event->output($corePath.\'elements/tv/inputproperties/\');\n    break;\n    //tv output\n    case \'OnTVOutputRenderList\':\n        $modx->event->output($corePath.\'elements/tv/output/\');\n    break;\n    case \'OnTVOutputRenderPropertiesList\':\n        $modx->event->output($corePath.\'elements/tv/outputproperties/\');\n    break;\n}\n\nreturn;', 0, NULL, 0, '', 0, ''),
(7, 1, 0, 'shk_multicurrency', 'Multicurrency in store.', 0, 5, 0, '/*\n plugin shk_multicurrency\n System event: OnLoadWebDocument, OnSHKgetProductPrice, OnSHKgetProductAdditParamPrice, OnSHKgetDeliveryPrice\n\n<div>\n    Валюта:\n    <select id="site_currency" name="curency">\n        <option value="1">руб.</option>\n        <option value="2">грн.</option>\n        <option value="3">USD</option>\n        <option value="4">euro</option>\n    </select>\n</div>\n\n*/\n\n$scriptProperties = array_merge(\n    array(\n        \'currency_default\' => $modx->getOption( \'shk3.currency_default\', null, 1 ),\n        \'currency_selected\' => $modx->getOption( \'shk3.currency_selected\', null, 0 )\n    ),\n    $scriptProperties\n);\n\n$shk_currency = !empty($_COOKIE[\'shk_currency\']) && is_numeric($_COOKIE[\'shk_currency\']) ? abs(intval($_COOKIE[\'shk_currency\'])) : $scriptProperties[\'currency_default\'];\n//Если нужен всегда преевод в одну валюту\nif( !empty( $scriptProperties[\'currency_selected\'] ) && $shk_currency != $scriptProperties[\'currency_selected\'] ){\n    setcookie( \'shk_currency\', intval($scriptProperties[\'currency_selected\']), time()+3600*24, "/" );\n    $shk_currency = intval($scriptProperties[\'currency_selected\']);\n}\n$currency_id = isset($_GET[\'scurr\']) && is_numeric($_GET[\'scurr\']) ? intval($_GET[\'scurr\']) : $shk_currency;\n\nif(!function_exists(\'shk_currency_calc\')){\n    function shk_currency_calc($properties, $base_price, $currency_id, $rate_ratio = 0){\n        \n        $inverse = isset($properties[\'inverse\']) ? $properties[\'inverse\'] : false;//обратный перевод цены\n        \n        if( !$rate_ratio ){\n            \n            if( isset( $_SESSION[\'shk_curr_rate\'] ) && is_numeric( $_SESSION[\'shk_curr_rate\'] ) && !$inverse ){\n                \n                $rate_ratio = $_SESSION[\'shk_curr_rate\'];\n                \n            }else{\n                \n                if( !isset( $properties[\'currency_rate\'] ) ){\n                    require_once MODX_CORE_PATH . "components/shopkeeper3/model/shopkeeper.class.php";\n                    $config = Shopkeeper::getConfig( array(\'currency_rate\') );\n                    $properties[\'currency_rate\'] = $config[\'currency_rate\'];\n                }\n                \n                $rate_ratio = 1;\n                \n                if( $properties[\'currency_default\'] != $currency_id ){\n                    \n                    $rate_default = 1;\n                    $rate = 1;\n                    //Определяем курс по умолчанию и новый курс\n                    foreach( $properties[\'currency_rate\'] as $rt ){\n                        if( $rt[\'id\'] == $properties[\'currency_default\'] ){\n                            $rate_default = Shopkeeper::cleanNumber( $rt[\'value\'], \'float\' );\n                        }\n                        else if( $rt[\'id\'] == $currency_id ){\n                            $rate = Shopkeeper::cleanNumber( $rt[\'value\'], \'float\' );\n                        }\n                    }\n                    \n                    if( !$inverse ){\n                        $rate_ratio = $rate_default / $rate;\n                        $_SESSION[\'shk_curr_rate\'] = $rate_ratio;\n                    }else{\n                        $rate_ratio = $rate / $rate_default;\n                    }\n                    \n                }\n                \n            }\n            \n        }\n        \n        //Считаем цену по курсу\n        if( $rate_ratio ){\n            \n            $price = $base_price * $rate_ratio;\n            $price = round( $price, ( ceil( $price ) == $price ? 0 : 2 ) );\n            return $price;\n        \n        }else{\n            return $base_price;\n        }\n        \n    }\n}\n\nswitch($modx->event->name){\n    \n    case \'OnLoadWebDocument\':\n        \n        if( !empty( $modx->placeholders[\'shk_currency\'] ) ) return \'\';\n        \n        if(empty($scriptProperties[\'noScript\'])){\n            \n            $script_str = \'\n            <script type="text/javascript">\n                var shk_cindex = document.cookie.indexOf("shk_currency=") > -1 ? document.cookie.indexOf("shk_currency=") + new String("shk_currency=").length : -1;\n                var shk_currency = shk_cindex > -1 ? document.cookie.substring(shk_cindex,shk_cindex+1) : 1;\n                jQuery("#site_currency")\n                .val(shk_currency)\n                .bind("change",function(){\n                    var loc_href = window.location.pathname+document.location.search;\n                    window.location.href = loc_href+(loc_href.indexOf("?") > -1 ? "&" : "?") + "scurr=" + this.value;\n                });\n            </script>\n            \';\n            $modx->regClientScript( $script_str, true );\n        }\n        \n        if( isset( $_GET[\'scurr\'] ) ){\n            \n            if( $currency_id != $shk_currency ){\n                \n                require_once MODX_CORE_PATH . "components/shopkeeper3/model/shopkeeper.class.php";\n                \n                $config = Shopkeeper::getConfig( array( \'currency_rate\', \'delivery\' ) );\n                $scriptProperties[\'currency_rate\'] = $config[\'currency_rate\'];\n                $conf_delivery = $config[\'delivery\'];\n                \n            }\n            \n            $_SESSION[\'shk_curr_rate\'] = null;\n            unset($_SESSION[\'shk_curr_rate\']);\n            \n            //Доставка\n            if( !empty( $conf_delivery ) ){\n                \n                $delivery_label = !empty( $_SESSION[\'shk_delivery\'][\'label\'] ) ? $_SESSION[\'shk_delivery\'][\'label\'] : \'\';\n                $delivery_price = !empty( $_SESSION[\'shk_delivery\'][\'price\'] ) ? $_SESSION[\'shk_delivery\'][\'price\'] : 0;\n                $_SESSION[\'shk_delivery\'] = array();\n                \n                foreach( $conf_delivery as $opt ){\n                    \n                    if( $opt[\'label\'] == $delivery_label ){\n                        \n                        $_SESSION[\'shk_delivery\'] = array(\n                            \'label\' => $opt[\'label\'],\n                            \'price\' => $delivery_price > 0 ? Shopkeeper::cleanNumber( $opt[\'price\'], \'float\' ) : 0,\n                            \'old_price\' => Shopkeeper::cleanNumber( $opt[\'price\'], \'float\' ),\n                            \'free_start\' => Shopkeeper::cleanNumber( $opt[\'free_start\'], \'float\' ),\n                            \'old_free_start\' => Shopkeeper::cleanNumber( $opt[\'free_start\'], \'float\' )\n                        );\n                        \n                        break;\n                    }\n                    \n                }\n                \n                if( !empty( $_SESSION[\'shk_delivery\'][\'price\'] ) ){\n                    $_SESSION[\'shk_delivery\'][\'price\'] = shk_currency_calc( $scriptProperties, $_SESSION[\'shk_delivery\'][\'price\'], $currency_id );\n                }\n                if( !empty( $_SESSION[\'shk_delivery\'][\'old_price\'] ) ){\n                    $_SESSION[\'shk_delivery\'][\'old_price\'] = shk_currency_calc( $scriptProperties, $_SESSION[\'shk_delivery\'][\'old_price\'], $currency_id );\n                }\n                if( !empty( $_SESSION[\'shk_delivery\'][\'free_start\'] ) ){\n                    $_SESSION[\'shk_delivery\'][\'free_start\'] = shk_currency_calc( $scriptProperties, $_SESSION[\'shk_delivery\'][\'free_start\'], $currency_id );\n                    $_SESSION[\'shk_delivery\'][\'old_free_start\'] = $_SESSION[\'shk_delivery\'][\'free_start\'];\n                }\n                \n            }\n            \n            $purchases = !empty( $_SESSION[\'shk_order\'] ) ? $_SESSION[\'shk_order\'] : array();\n            \n            if( !empty( $scriptProperties[\'currency_rate\'] ) ){\n                \n                //Изменяем цены товаров в корзине\n                if( !empty( $purchases ) ){\n                    \n                    foreach( $purchases as $key => &$purchase ){\n                        \n                        if( isset( $purchase[\'old_price\'] ) ){\n                            $base_price = $purchase[\'old_price\'];\n                        }\n                        else if( !isset( $purchase[\'old_price\'] ) ) {\n                            $purchase[\'old_price\'] = $purchase[\'price\'];\n                            $base_price = $purchase[\'price\'];\n                        }\n                        \n                        $purchase[\'price\'] = shk_currency_calc( $scriptProperties, $base_price, $currency_id );\n                        \n                        //Доп. параметры\n                        if( !empty( $purchase[\'options\'] ) ){\n                            foreach( $purchase[\'options\'] as &$addit_param ){\n                                \n                                if( !isset( $addit_param[3] ) ) $addit_param[3] = $addit_param[1];\n                                $addit_param[1] = shk_currency_calc( $scriptProperties, $addit_param[3], $currency_id );\n                                \n                            }\n                        }\n                        \n                    }\n                    \n                    $_SESSION[\'shk_order\'] = $purchases;\n                    \n                }else{\n                    \n                    //Если нет товаров, просто переключаем валюту\n                    shk_currency_calc( $scriptProperties, 0, $currency_id );\n                    \n                }\n                \n                $shk_currency = $currency_id;\n                setcookie( \'shk_currency\', $shk_currency, time()+3600*24, "/" );\n                \n                //Сохраняем название валюты\n                $currency_name = \'\';\n                foreach( $scriptProperties[\'currency_rate\'] as $rt ){\n                    if( $rt[\'id\'] == $currency_id ){\n                        $currency_name = $rt[\'label\'];\n                        break;\n                    }\n                }\n                $_SESSION[\'shk_currency_name\'] = $currency_name;\n                \n            }\n            \n            $back_url = !empty($_SERVER[\'HTTP_REFERER\']) ? $_SERVER[\'HTTP_REFERER\'] : $modx->makeURL($modx->resource->get(\'parent\'),\'\',\'\',\'abs\');\n            if( $modx->config[\'friendly_urls\'] ){\n                if( strpos($back_url,\'?\') !== false ) $back_url = substr( $back_url, 0, strpos($back_url,\'?\') );\n            }\n            \n            $modx->sendRedirect( $back_url, 0 );\n            \n        }\n        \n        $currency_name = !empty($_SESSION[\'shk_currency_name\']) ? $_SESSION[\'shk_currency_name\'] : \'\';\n        if( !$currency_name ){\n            $currency_name = $modx->getOption(\'shk3.currency\',null,\'\');\n        }\n        $modx->setPlaceholder( \'shk_currency\', $currency_name );\n        \n    break;\n    case \'OnSHKgetProductAdditParamPrice\':\n    case \'OnSHKgetDeliveryPrice\':\n    case \'OnSHKgetProductPrice\':\n        \n        $output = $modx->getOption( \'price\', $scriptProperties, 0 );\n        \n        if( is_numeric( $output ) ){\n            \n            //Считаем цену по курсу\n            $output = shk_currency_calc( $scriptProperties, $output, $currency_id );\n            \n            $modx->event->_output = \'\';\n            $modx->event->output( $output );\n            \n        }else{\n            $modx->event->output( false );\n        }\n        \n    break;\n    \n}\n\nreturn \'\';', 0, 'a:0:{}', 0, '', 0, ''),
(8, 1, 0, 'tmRefresh', 'Refresh tagManager filters data.', 0, 0, 0, '/**\n * tmRefresh\n *\n * System event: OnDocFormSave, OnCacheUpdate\n *\n */\n\n$eventName = $modx->event->name;\n\nif( !function_exists( \'get_filter_categories\' ) ){\n    \n    function get_filter_categories(){\n        \n        global $modx;\n        \n        $modelpath = $modx->config[\'core_path\'].\'components/tag_manager2/model/\';\n        $modx->addPackage(\'tag_manager2\', $modelpath);\n        \n        $f_categories = array();\n        $c = $modx->newQuery("tagManager");\n        $c->select(array(\'id\',\'category\'));\n        $c->groupby(\'category\');\n        if ($c->prepare() && $c->stmt->execute()) {\n            \n            while ($row = $c->stmt->fetch(PDO::FETCH_ASSOC)) {\n                \n                array_push( $f_categories, $row[\'category\'] );\n                \n            }\n            \n        }\n        \n        return $f_categories;\n        \n    }\n    \n}\n\nif( $eventName == \'OnDocFormSave\' ){\n\n    $page_id = $resource->get(\'id\');\n    $page_parent = $resource->get(\'parent\');\n    $isfolder = $resource->get(\'isfolder\');\n    $catalog_root = $modx->getOption(\'tag_mgr2.catalog_id\', null, 0);\n    \n    if( !$isfolder ){\n        \n        $f_categories = get_filter_categories();\n        \n        if( in_array( $page_parent, $f_categories ) ){\n            \n            $response = $modx->runProcessor(\'updatefilters\',\n                array(\n                   \'parent_id\' => $page_parent,\n                   \'clear_cache\' => false\n                ),\n                array(\'processors_path\' => $modx->config[\'core_path\'] . \'components/tag_manager2/processors/mgr/\')\n            );\n            if ($response->isError()) {\n                return $response->getMessage();\n            }\n            \n        }\n        \n    }\n    \n}else if( $eventName == \'OnCacheUpdate\' ){\n    \n    //run in background\n    @exec( "php -f \'" . $modx->config[\'assets_path\'] . "components/tag_manager2/cron_updatefilters.php\' > /dev/null 2>&1 &" );\n    \n}\n\n//$modx->event->output(\'\');', 0, 'a:0:{}', 0, '', 1, 'core/components/tag_manager2/elements/plugins/plugin.tm_refresh.php'),
(9, 0, 0, 'TinyMCERTE', '', 0, 11, 0, '/**\n * TinyMCE Rich Tech Editor\n *\n */\n$corePath = $modx->getOption(\'tinymcerte.core_path\', null, $modx->getOption(\'core_path\', null, MODX_CORE_PATH) . \'components/tinymcerte/\');\n/** @var TinyMCERTE $tinymcerte */\n$tinymcerte = $modx->getService(\n    \'tinymcerte\',\n    \'TinyMCERTE\',\n    $corePath . \'model/tinymcerte/\',\n    array(\n        \'core_path\' => $corePath\n    )\n);\n\n$className = \'TinyMCERTE\' . $modx->event->name;\n$modx->loadClass(\'TinyMCERTEPlugin\', $tinymcerte->getOption(\'modelPath\') . \'tinymcerte/events/\', true, true);\n$modx->loadClass($className, $tinymcerte->getOption(\'modelPath\') . \'tinymcerte/events/\', true, true);\nif (class_exists($className)) {\n    /** @var TinyMCERTEPlugin $handler */\n    $handler = new $className($modx, $scriptProperties);\n    $handler->run();\n}\nreturn;', 0, 'a:0:{}', 0, '', 0, ''),
(10, 0, 0, 'migx_autoalias', '', 0, 5, 0, '/**\n * migx_autoalias\n *\n * OnDocFormSave\n *\n */\n\n/*\n\n$modx->invokeEvent( \'OnDocFormSave\', array( \'id\' => $object->get(\'id\'), \'object\' => &$object ) );\n\n*/\n\n$namefield = $modx->getOption( \'namefield\', $scriptProperties, \'pagetitle\' );\n\nif( $object->get(\'resource_id\') ){\n    \n    if( !$object->get(\'alias\') ){\n        \n        $resource = $modx->newObject(\'modResource\');\n        $alias = $resource->cleanAlias( $object->get( $namefield ) );\n        \n        $object->set( \'alias\', $alias );\n        $object->save();\n        \n    }\n    \n}\n\nreturn \'\';', 0, 'a:0:{}', 0, '', 0, '');
INSERT INTO `modx_site_plugins` (`id`, `source`, `property_preprocess`, `name`, `description`, `editor_type`, `category`, `cache_type`, `plugincode`, `locked`, `properties`, `disabled`, `moduleguid`, `static`, `static_file`) VALUES
(13, 0, 0, 'giftCards', 'Подарочные карты и скидки.', 0, 0, 0, '/*\n\ngiftCards\n\nСобытия:\nOnSHKcalcTotalPrice, OnSHKsaveOrder, OnSHKChangeStatus, OnSHKgetProductPrice, OnWebLogin, OnWebLogout\n\n[[+shk.price_without_discount]] - Цена без скидки\n\n*/\n\nswitch($modx->event->name) {\n    \n    //Если введён код подарочной карты, вычитаем её номинал из цены заказа и возвращаем новую цену\n    case \'OnSHKcalcTotalPrice\':\n        \n        $price_total = (float) str_replace(array(\' \',\',\'),array(\'\',\'.\'),$modx->getOption(\'price_total\',$scriptProperties,0));\n        \n        $modx->setPlaceholder(\'shk.price_without_discount\',$price_total);\n        \n        if($price_total && isset($_SESSION[\'shk_orderdata\'][\'giftcard_amount\'])){\n            \n            $order_discount = (int) str_replace(array(\' \',\',\'),array(\'\',\'.\'),$_SESSION[\'shk_orderdata\'][\'giftcard_amount\']);\n            \n            if($order_discount){\n                \n                $price_total = max( 0, round(($price_total - $order_discount),2) );\n                \n            }\n            \n        }\n        \n        $modx->event->output($price_total);\n\n    break;\n\n    //Если введён код подарочной карты, ставим заметку заказу и очищаем сессии карты при отправке заказа\n    case \'OnSHKsaveOrder\':\n        \n        $card_code = isset($_SESSION[\'shk_orderdata\'][\'giftcard\']) ? $_SESSION[\'shk_orderdata\'][\'giftcard\'] : \'\';\n        $card_money = isset($_SESSION[\'shk_orderdata\'][\'giftcard_amount\']) ? $_SESSION[\'shk_orderdata\'][\'giftcard_amount\'] : 0;\n        \n        if(isset($_SESSION[\'shk_orderdata\'][\'discount\'])){\n            $discount = (int) $_SESSION[\'shk_orderdata\'][\'discount\'];\n        }else{\n            $discount = (int) $modx->runSnippet(\'userDiscount\');\n        }\n        \n        if( ( $card_code && $card_money ) || $discount ){\n            $modx->addPackage(\'shopkeeper3\',MODX_CORE_PATH."components/shopkeeper3/model/");\n            \n            $order_id = $modx->getOption(\'order_id\',$scriptProperties,0);\n            if($order_id && ($order = $modx->getObject(\'shk_order\',array(\'id\'=>$order_id)))){\n                \n                $note = $order->get(\'note\');\n                $options = $order->get(\'options\');\n                $options = $options ? json_decode($options,true) : array();\n                \n                if($card_code) {\n                    $note .= ($note ? \' \' : \'\')."{$card_code} ({$card_money})";\n                    $options[\'gift\'] = min( $card_money, $order->get(\'price\') );\n                }\n                if($discount){\n                    $note .= ($note ? \' \' : \'\')."- {$discount}%";\n                    $options[\'discount\'] = $discount;\n                }\n                $order->set(\'note\',$note);\n                $order->set(\'options\', ( $options ? json_encode($options) : \'\' ) );\n                $order->save();\n                \n                $_SESSION[\'shk_orderdata\'][\'giftcard_amount\'] = \'\';\n                $_SESSION[\'shk_orderdata\'][\'giftcard\'] = \'\';\n                $_SESSION[\'shk_orderdata\'][\'discount\'] = \'\';\n                unset($_SESSION[\'shk_orderdata\'][\'giftcard_amount\'],$_SESSION[\'shk_orderdata\'][\'giftcard\'],$_SESSION[\'shk_orderdata\'][\'discount\']);\n                \n            }\n        }\n        \n    break;\n    \n    //При смене статуса заказа на "Выполнен" сохраняем статистику для пользователя\n    case \'OnSHKChangeStatus\':\n        \n        $modx->addPackage(\'giftcards\', $modx->getOption(\'core_path\').\'components/giftcards/model/\');\n        \n        $order_ids = $modx->getOption(\'order_ids\',$scriptProperties,0);\n        $status = $modx->getOption(\'status\',$scriptProperties,0);\n        $status_done = $modx->getOption(\'giftcards.status_done\', null, 3);\n        $user_stat_field = $modx->getOption(\'giftcards.user_stat_field\', null, \'order_stat\');\n        \n        //Обновляем статистику покупок пользователя\n        if( !empty( $order_ids ) && $status == $status_done ){\n            \n            $orders = $modx->getCollection( \'shk_order\', array( \'id:IN\' => $order_ids ) );\n            \n            foreach( $orders as $order ){\n                \n                if ( $userid = $order->get(\'userid\') ){\n                    \n                    $user = $modx->getObject(\'modUser\',$userid);\n                    $profile = $user->getOne(\'Profile\');\n                    \n                    $fields_ext = $profile->get(\'extended\');\n                    $user_stat = (float) str_replace(array(\' \',\',\'),array(\'\',\'.\'),$modx->getOption($user_stat_field, $fields_ext, 0));\n                    $user_stat += (float) str_replace(array(\' \',\',\'),array(\'\',\'.\'),$order->get(\'price\'));\n                    $fields_ext[$user_stat_field] = $user_stat;\n                    \n                    $profile->set(\'extended\',$fields_ext);\n                    $profile->save();\n                    \n                    //Проверяем достигнута ли сумма покупок, которая даёт право на скидку\n                    //и переводим пользователя в соответствующую группу\n                    $c = $modx->newQuery(\'giftCardsDiscount\');\n                    $c->select($modx->getSelectColumns(\'giftCardsDiscount\'));\n                    $c->where(array(\'condition:<=\' => round($user_stat)));\n                    $c->sortby($modx->escape(\'condition\'),\'DESC\');\n                    $dsc_groups = $modx->getCollection(\'giftCardsDiscount\',$c);\n                    \n                    if(count($dsc_groups)>0){\n                        \n                        $new_dsc_group = current($dsc_groups);\n                        $group = $modx->getObject(\'modUserGroup\',array(\'id\'=>$new_dsc_group->get(\'id_group\')));\n                        if($group){\n                            \n                            $userGroups = $user->getUserGroups();\n                            \n                            if(!$user->isMember($group->get(\'name\'))){\n                                \n                                //Ищем все группы пользователя, которым назначены скидки\n                                if(count($userGroups)>0){\n                                    \n                                    $cc = $modx->newQuery(\'giftCardsDiscount\');\n                                    $cc->select($modx->getSelectColumns(\'giftCardsDiscount\'));\n                                    $cc->where(array(\'id_group:IN\' => $userGroups, \'id_group:!=\' => $group->get(\'id\')));\n                                    $ugroups = $modx->getCollection(\'modUserGroup\',$cc);\n                                    \n                                    foreach($ugroups as $ugroup){\n                                        $user->leaveGroup($ugroup->get(\'id\'));\n                                    }\n                                    \n                                }\n                                \n                                //Добавляем пользователя в новую группу со скидкой\n                                $user->joinGroup($group->get(\'id\'));\n                                \n                            }\n                            \n                        }\n                        \n                    }\n                    \n                }\n                \n            }\n            \n        }\n        \n    break;\n    \n    //Возвращает цену товара с учётом скидки пользователя\n    case \'OnSHKgetProductPrice\':\n        \n        $price = $modx->getOption(\'price\',$scriptProperties,0);\n        if(isset($_SESSION[\'shk_orderdata\'][\'discount\'])){\n            $discount = (int) $_SESSION[\'shk_orderdata\'][\'discount\'];\n        }else{\n            $discount = (int) $modx->runSnippet(\'userDiscount\');\n        }\n        \n        if($discount > 0){\n            $price = round( $price * ( 1 - $discount / 100 ), 2 );\n        }\n        \n        $modx->event->output($price);\n        \n    break;\n    \n    //При авторизации пересчитываем цены в корзине с учётом скидки\n    case \'OnWebLogin\':\n        \n        $curSavedP = !empty( $_SESSION[\'shk_order\'] ) ? $_SESSION[\'shk_order\'] : array();\n        if( isset( $_SESSION[\'shk_orderdata\'][\'discount\'] ) ){\n            $discount = (int) $_SESSION[\'shk_orderdata\'][\'discount\'];\n        }else{\n            $discount = (int) $modx->runSnippet(\'userDiscount\', array(\'user_id\' => $user->get(\'id\')));\n        }\n        \n        if($discount){\n            foreach( $curSavedP as &$purchase ){\n                \n                if( $purchase[\'price\'] > 0 ){\n                    $temp_price = $purchase[\'price\'];\n                    $purchase[\'price\'] = max( 0, round( $temp_price * ( 1 - $discount / 100 ), 2 ) );\n                    $purchase[\'old_price\'] = $temp_price;\n                }\n                \n                //options\n                if( !empty( $purchase[ \'options\' ] ) && is_array( $purchase[ \'options\' ] ) ){\n                    \n                    foreach( $purchase[ \'options\' ] as &$options ){\n                        if( !$options[0] ) continue;\n                        $temp_price = $options[0];\n                        $options[0] = max( 0, round( $temp_price * ( 1 - $discount / 100 ), 2 ) );\n                        $options[3] = $temp_price;\n                    }\n                    \n                }\n                \n            }\n        }\n        \n        $_SESSION[\'shk_order\'] = $curSavedP;\n        \n    break;\n    \n    \n    //При выходе пользователя возвращаем цены без скидок\n    case \'OnWebLogout\':\n        \n        $curSavedP = !empty($_SESSION[\'shk_order\']) ? $_SESSION[\'shk_order\'] : array();\n        \n        foreach( $curSavedP as &$purchase ){\n            \n            if( isset( $purchase[\'old_price\'] ) ){\n                \n                $purchase[\'price\'] = $purchase[\'old_price\'];\n                unset($purchase[\'old_price\']);\n                \n                //options\n                if( !empty( $purchase[ \'options\' ] ) && is_array( $purchase[ \'options\' ] ) ){\n                    \n                    foreach( $purchase[ \'options\' ] as &$options ){\n                        if( !isset( $options[3] ) ) continue;\n                        $options[0] = $options[3];\n                        unset($options[3]);\n                    }\n                    \n                }\n                \n            }\n            \n        }\n        \n        $_SESSION[\'shk_order\'] = $curSavedP;\n        $_SESSION[\'shk_orderdata\'][\'discount\'] = \'\';\n        unset($_SESSION[\'shk_orderdata\'][\'discount\']);\n        \n    break;\n    \n}\n\nreturn \'\';', 0, NULL, 0, '', 0, '');
