(28, 0, 0, 'migxIsNewObject', '', 0, 4, 0, 'if (isset($_REQUEST[\'object_id\']) && $_REQUEST[\'object_id\']==\'new\'){\n    return 1;\n}', 0, NULL, '', 0, ''),
(29, 0, 0, 'phpthumbon', 'Создание превьюх картинок', 0, 0, 0, '/**\r\n * phpThumbOn\r\n * Создание превьюх картинок\r\n *\r\n * Copyright 2013 by Agel_Nash <Agel_Nash@xaker.ru>\r\n *\r\n * @category images\r\n * @license GNU General Public License (GPL), http://www.gnu.org/copyleft/gpl.html\r\n * @author Agel_Nash <Agel_Nash@xaker.ru>\r\n */\r\n\r\nif(empty($modx) || !($modx instanceof modX)) return \'\';\r\n\r\n$componentPath = (string)$modx->getOption(\'phpthumbon.core_path\', null, $modx->getOption(\'core_path\').\'components/phpthumbon/\');\r\n\r\nif(!isset($modx->phpThumbOn)){\r\n    $modx->phpThumbOn = $modx->getService("phpthumbon","phpThumbOn",$componentPath.\'model/phpthumbon/\', $scriptProperties);\r\n}\r\n\r\nif(!($flag = ($modx->phpThumbOn instanceof phpThumbOn))){\r\n    $modx->phpThumbOn = null;\r\n}\r\nreturn $flag ? $modx->phpThumbOn->run($scriptProperties) : $modx->getOption(\'phpthumbon.noimage\', $scriptProperties);', 0, 'a:2:{s:5:"input";a:7:{s:4:"name";s:5:"input";s:4:"desc";s:16:"phpthumbon.input";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";s:21:"phpthumbon:properties";s:4:"area";s:0:"";}s:7:"options";a:7:{s:4:"name";s:7:"options";s:4:"desc";s:17:"phpthumbon.folder";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";s:21:"phpthumbon:properties";s:4:"area";s:0:"";}}', '', 0, ''),
(30, 3, 0, 'QuickCrumbs', '<strong>1.0.1-pl</strong> A quick and efficient bread crumbs snippet for MODx Revolution', 0, 0, 0, '/**\n * QuickCrumbs\n *\n * Copyright 2010-2012 by MODx, LLC\n *\n * QuickCrumbs is free software; you can redistribute it and/or modify it under the\n * terms of the GNU General Public License as published by the Free Software\n * Foundation; either version 2 of the License, or (at your option) any later\n * version.\n *\n * QuickCrumbs is distributed in the hope that it will be useful, but WITHOUT ANY\n * WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS FOR\n * A PARTICULAR PURPOSE. See the GNU General Public License for more details.\n *\n * You should have received a copy of the GNU General Public License along with\n * QuickCrumbs; if not, write to the Free Software Foundation, Inc., 59 Temple\n * Place, Suite 330, Boston, MA 02111-1307 USA\n *\n * @package quickcrumbs\n */\n/**\n * A quick and efficient bread crumbs Snippet for MODx Revolution.\n *\n * @package quickcrumbs\n */\n$output = array();\n$parentTitles = array();\n$resourceId = (integer)$modx->getOption(\'resourceId\', $scriptProperties, 0);\nif (!$resourceId) $resourceId = (integer)$modx->resource->get(\'id\');\n$fields = empty($fields) ? \'pagetitle,menutitle,description\' : $fields;\n$fields = explode(\',\', $fields);\nforeach ($fields as $fieldKey => $field) $fields[$fieldKey] = trim($field);\narray_unshift($fields, \'id\', \'class_key\', \'context_key\');\n$tvs = empty($tvs) ? \'\' : $tvs;\n$tvs = explode(\',\', $tvs);\nforeach ($tvs as $tvKey => $tv) $tvs[$tvKey] = trim($tv);\n$parents = $modx->getParentIds($resourceId);\narray_pop($parents);\n$parents = array_reverse($parents);\narray_push($parents, $resourceId);\n$siteStart = (integer)$modx->getOption(\'site_start\', null, 1);\n\n//Убираем активную если есть добавление из другого сниппета\n$addcrumb = $modx->getOption(\'addcrumb\', $scriptProperties, \'\');\n$addcrumb_on = false;\nif ($addcrumb) {\n    $addcrumb = array_map(\'trim\', explode(\',\', $addcrumb));\n    foreach ($addcrumb as $acrumb) {\n        if (!empty($_GET[$acrumb])) {\n            $addcrumb_on = true;\n        }\n    }\n}\n\nif (!in_array($siteStart, $parents) && !empty($showSiteStart)) {\n    array_unshift($parents, $siteStart);\n}\nif (empty($siteStartTpl)) {\n    $siteStartTpl = $tpl;\n}\nif (empty($selfTpl)) {\n    $selfTpl = $tpl;\n}\nif ($siteStart == $resourceId && !empty($showSelf)) {\n    $siteStartTpl = $selfTpl;\n}\n\nif (!empty($parents)) {\n    $query = $modx->newQuery(\'modResource\', array(\'id:IN\' => $parents, \'published\' => 1, \'deleted\' => 0));\n    if (!empty($excludeHidden)) {\n        $query->where(array(\'hidemenu\' => 0));\n    }\n    if (!empty($hideEmptyContainers)) {\n        $query->where(array(\n            \'content:!=\' => \'\',\n            \'class_key:NOT IN\' => array(\'modWebLink\', \'modSymLink\')\n        ));\n    }\n    if (!empty($hideIds)) {\n        $query->where(array(\'id:NOT IN\' => explode(\',\', $hideIds)));\n    }\n    $query->select($modx->getSelectColumns(\'modResource\', \'\', \'\', $fields));\n    $collection = $modx->getCollection(\'modResource\', $query);\n    $top = true;\n    $crumb = 1;\n    $maxCrumbs = !empty($maxCrumbs) ? (integer)$maxCrumbs : 0;\n    $totalCrumbs = count($parents);\n    $skip = $maxCrumbs > 0 ? ($totalCrumbs - $maxCrumbs - 2) : 0;\n    $parent = reset($parents);\n    while ($parent) {\n        $object = reset($collection);\n        while ($object && (integer)$object->get(\'id\') !== (integer)$parent) {\n            $object = next($collection);\n        }\n        if ($object) {\n            $properties = array_merge($scriptProperties, $object->get($fields));\n            foreach ($tvs as $tvKey => $tv) {\n                $properties = array_merge($properties, array(\'tv.\' . $tv => $object->getTVValue($tv)));\n            }\n            $self = $object->get(\'id\') === $resourceId;\n            $skipped = false;\n            if (!$top && !$self && $skip > 0) {\n                if ($skipFromTop) {\n                    if (!empty($skipTpl)) {\n                        $output[] = $modx->getChunk($skipTpl, $properties);\n                    }\n                    $skipped = true;\n                    $skip--;\n                } elseif ($totalCrumbs - $crumb <= $skip) {\n                    if (!empty($skipTpl)) {\n                        $output[] = $modx->getChunk($skipTpl, $properties);\n                    }\n                    $skipped = true;\n                    $skip--;\n                }\n            }\n            if (!$skipped) {\n                if ((integer)$parent == $siteStart && !empty($showSiteStart)) {\n                    if (!empty($siteStartTpl)) {\n                        $output[] = $modx->getChunk($siteStartTpl, $properties);\n                    } else {\n                        $output[] = "<pre>" . print_r($properties, true) . "</pre>";\n                    }\n                } elseif ($self && !empty($showSelf) && !$addcrumb_on) {\n                    if (!empty($selfTpl)) {\n                        $output[] = $modx->getChunk($selfTpl, $properties);\n                    } else {\n                        $output[] = "<pre>" . print_r($properties, true) . "</pre>";\n                    }\n                } else {\n                    $parentTitles[] = $properties[\'pagetitle\'];\n                    if (!empty($tpl)) {\n                        $output[] = $modx->getChunk($tpl, $properties);\n                    } else {\n                        $output[] = "<pre>" . print_r($properties, true) . "</pre>";\n                    }\n                }\n            }\n        }\n        $parent = next($parents);\n        $crumb++;\n        $top = false;\n    }\n}\n$separator = isset($separator) ? "{$separator}" : "&nbsp;&raquo;&nbsp;";\n$output = implode($separator, $output);\nif (!empty($outerTpl)) {\n    $output = $modx->getChunk($outerTpl, array(\'crumbs\' => $output));\n}\nif (!empty($parentTitlesPlaceholder) && !empty($parentTitles)) {\n    if (empty($titleSeparator)) {\n        $titleSeparator = \' - \';\n    }\n    if (!empty($parentTitlesReversed)) {\n        $parentTitles = array_reverse($parentTitles);\n    }\n    $parentTitles = implode($titleSeparator, $parentTitles);\n    $modx->setPlaceholder($parentTitlesPlaceholder, $titleSeparator . $parentTitles);\n}\nif (!empty($toPlaceholder)) {\n    $modx->setPlaceholder($toPlaceholder, $output);\n    return \'\';\n} else {\n    return $output;\n}', 0, 'a:20:{s:5:"debug";a:7:{s:4:"name";s:5:"debug";s:4:"desc";s:68:"If true, will send the SQL query to the MODx log. Defaults to false.";s:4:"type";s:13:"combo-boolean";s:7:"options";a:0:{}s:5:"value";b:0;s:7:"lexicon";N;s:4:"area";s:0:"";}s:13:"excludeHidden";a:7:{s:4:"name";s:13:"excludeHidden";s:4:"desc";s:81:"If true, Resources that are hidden from menus will be skipped in the breadcrumbs.";s:4:"type";s:13:"combo-boolean";s:7:"options";a:0:{}s:5:"value";b:0;s:7:"lexicon";N;s:4:"area";s:0:"";}s:6:"fields";a:7:{s:4:"name";s:6:"fields";s:4:"desc";s:157:"The fields to get from the resource as placeholders; NOTE that id, class_key, and context_key are always selected. (DEFAULT: pagetitle,menutitle,description)";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:31:"pagetitle,menutitle,description";s:7:"lexicon";N;s:4:"area";s:0:"";}s:19:"hideEmptyContainers";a:7:{s:4:"name";s:19:"hideEmptyContainers";s:4:"desc";s:99:"If true, will skip Resources that are empty containers (or are WebLink/SymLink) in the breadcrumbs.";s:4:"type";s:13:"combo-boolean";s:7:"options";a:0:{}s:5:"value";b:0;s:7:"lexicon";N;s:4:"area";s:0:"";}s:7:"hideIds";a:7:{s:4:"name";s:7:"hideIds";s:4:"desc";s:61:"A list of resource IDs you want to hide from the breadcrumbs.";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";N;s:4:"area";s:0:"";}s:9:"maxCrumbs";a:7:{s:4:"name";s:9:"maxCrumbs";s:4:"desc";s:207:"The maximum number of crumbs to display between the top crumb and self. Additional crumbs are skipped (see skipTpl and skipFromTop properties). 0 (or an empty string) indicates no maximum limit: the default.";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:1:"0";s:7:"lexicon";N;s:4:"area";s:0:"";}s:8:"outerTpl";a:7:{s:4:"name";s:8:"outerTpl";s:4:"desc";s:163:"Name of an optional chunk serving as wrapper template for the snippet output; not used if empty. The placeholder for the output is set as crumbs, i.e. [[+crumbs]].";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";N;s:4:"area";s:0:"";}s:23:"parentTitlesPlaceholder";a:7:{s:4:"name";s:23:"parentTitlesPlaceholder";s:4:"desc";s:127:"If set, pagetitles of all the parent breadcrumbs are compiled and set as a placeholder for use in the content of your Resource.";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";N;s:4:"area";s:0:"";}s:20:"parentTitlesReversed";a:7:{s:4:"name";s:20:"parentTitlesReversed";s:4:"desc";s:88:"If set, the parentTitlesPlaceholder output is returned with pagetitles in reverse-order.";s:4:"type";s:13:"combo-boolean";s:7:"options";a:0:{}s:5:"value";b:0;s:7:"lexicon";N;s:4:"area";s:0:"";}s:7:"selfTpl";a:7:{s:4:"name";s:7:"selfTpl";s:4:"desc";s:70:"Name of a chunk serving as resource template for the current resource.";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";N;s:4:"area";s:0:"";}s:9:"separator";a:7:{s:4:"name";s:9:"separator";s:4:"desc";s:37:"Separator to use (default is &raquo;)";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:19:"&nbsp;&raquo;&nbsp;";s:7:"lexicon";N;s:4:"area";s:0:"";}s:8:"showSelf";a:7:{s:4:"name";s:8:"showSelf";s:4:"desc";s:75:"If true, will show the current resource as the final crumb. (DEFAULT: true)";s:4:"type";s:13:"combo-boolean";s:7:"options";a:0:{}s:5:"value";b:1;s:7:"lexicon";N;s:4:"area";s:0:"";}s:13:"showSiteStart";a:7:{s:4:"name";s:13:"showSiteStart";s:4:"desc";s:72:"If true, will render the site_start resource as a crumb. (DEFAULT: true)";s:4:"type";s:13:"combo-boolean";s:7:"options";a:0:{}s:5:"value";b:1;s:7:"lexicon";N;s:4:"area";s:0:"";}s:12:"siteStartTpl";a:7:{s:4:"name";s:12:"siteStartTpl";s:4:"desc";s:64:"Name of a chunk serving as resource template for the site_start.";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";N;s:4:"area";s:0:"";}s:11:"skipFromTop";a:7:{s:4:"name";s:11:"skipFromTop";s:4:"desc";s:137:"If true (the default), crumbs will be be skipped from the top crumb when maxCrumbs is exceeded. Otherwise they will be skipped from self.";s:4:"type";s:13:"combo-boolean";s:7:"options";a:0:{}s:5:"value";b:1;s:7:"lexicon";N;s:4:"area";s:0:"";}s:7:"skipTpl";a:7:{s:4:"name";s:7:"skipTpl";s:4:"desc";s:77:"The name of a chunk to use for each crumb skipped when maxCrumbs is exceeded.";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:1:"0";s:7:"lexicon";N;s:4:"area";s:0:"";}s:14:"titleSeparator";a:7:{s:4:"name";s:14:"titleSeparator";s:4:"desc";s:77:"A separator to use in between pagetitles used in the parentTitlesPlaceholder.";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:" - ";s:7:"lexicon";N;s:4:"area";s:0:"";}s:13:"toPlaceholder";a:7:{s:4:"name";s:13:"toPlaceholder";s:4:"desc";s:125:"If set, a placeholder by the specified name with be set containing the output and no output will be returned by this snippet.";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";N;s:4:"area";s:0:"";}s:3:"tpl";a:7:{s:4:"name";s:3:"tpl";s:4:"desc";s:121:"Name of a chunk serving as a resource template. NOTE: if not provided, properties are dumped to output for each resource.";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";N;s:4:"area";s:0:"";}s:3:"tvs";a:7:{s:4:"name";s:3:"tvs";s:4:"desc";s:170:"The template variables to get from the resource as placeholders; Example: mytv; NOTE to use inside templat